import React, { Component } from "react";
import {Row, Container,Card,Col,Button} from 'react-bootstrap';
import "./addListStyle.scss";
class AddList extends Component {
  state = {
    showForm: false
  }

  addTodo =()=>{
    return(
      <Row>
      <Row >
      <Col  className="addlistmaincol">
        <div className="form-group">
          <label htmlFor="example2">Title</label>
          <input type="text" id="example2" className="form-control form-control-md" placeholder="Enter Title"/>
        </div>

        <div className="form-group">
          <label htmlFor="example2">Text</label>
          <input type="text" id="example2" className="form-control form-control-md" placeholder="Enter Text"/>
        </div>
      </Col>
    </Row>

    <Row >
      <Col>
      <Button variant="primary">Submit</Button>
      </Col>
    </Row>

    </Row>

    )
  }

  render() {
    const {showForm}=this.state;
    return (
    <Container className="addlistwrapper">

      {showForm === true ?

     this.addTodo()
      : 
      <Row >
      <Col>
      <Button variant="primary" onClick={()=> this.setState({showForm:true})}>Add Todo List</Button>
      </Col>
    </Row>}
    </Container>

    );
  }
}

export default (AddList);
